mapper.select:
SELECT t.*,@rownum := @rownum + 1 AS rownum FROM ( SELECT @rownum := 0, t1.ID AS id, t1.USER_NAME AS userName, t1.TRUE_NAME AS trueName, t1.PASS_WORD AS password, t1.QUESTION1 AS question1, t1.ANSWER1 AS answer1, t1.QUESTION2 AS question2, t1.ANSWER2 AS answer2, t2.COMPANY_NAME AS companyName, t1.COMPANY_ID AS companyId, DATE_FORMAT(t1.CREATE_DATE,'%y-%m-%d %T 24') AS createDateStr, t1.STATUS AS status FROM BUS_USER t1,BUS_COMPANY t2 WHERE t1.COMPANY_ID = t2.ID
AND t1.USER_NAME LIKE concat('%',#{userName},'%')
AND t1.TRUE_NAME LIKE concat('%',#{trueName},'%')
AND t2.COMPANY_NAME LIKE concat('%',#{companyName},'%')
) t
LIMIT #{begin},#{end}
