mapper.select:
SELECT @rownum := 0, t1.ID AS id, t1.USER_NAME AS userName, t1.TRUE_NAME AS trueName, t1.PASS_WORD AS password, t1.QUESTION1 AS question1, t1.ANSWER1 AS answer1, t1.QUESTION2 AS question2, t1.ANSWER2 AS answer2, t1.COMPANY_NAME AS companyName, t1.COMPANY_CODE AS companyCode, t1.INVOICE_ADRESS AS invoiceAdress, t1.REPORT_ADRESS AS reportAdress, t1.E_MAIL AS email, t1.TELPHONE AS telphone, DATE_FORMAT(t1.CREATE_DATE,'%y-%m-%d %T 24') AS createDateStr, t1.STATUS AS status FROM BUS_USER t1 WHERE STATUS = '1'
AND t1.USER_NAME = #{userName}
AND t1.PASS_WORD = #{password}

select p.* from( select rownum,rownum as rn,a.* from ( SELECT T.TABLE_NAME, T.COLUMN_NAME, T.DATA_TYPE, T.DATA_LENGTH, T.NULLABLE, T2.COMMENTS AS tableComments, T1.COMMENTS FROM USER_TAB_COLUMNS T, USER_COL_COMMENTS T1, USER_TAB_COMMENTS T2
AND T.TABLE_NAME = T1.TABLE_NAME(+) AND T.COLUMN_NAME = T1.COLUMN_NAME(+) AND T.TABLE_NAME = T2.TABLE_NAME(+)
order by ${orderByString}
order by T.TABLE_NAME, T.COLUMN_ID
) a
where rownum <= #{end,jdbcType=INTEGER}
) p
where rn >= #{begin, jdbcType=INTEGER}

select p.* from(
select
rownum,rownum as rn,a.* from (
  select
    TABLE_NAME,
    COMMENTS
from user_tab_comments T


    order by ${orderByString}


    order by T.TABLE_NAME

) a

    where rownum <= #{end,jdbcType=INTEGER}

) p

    where rn >= #{begin, jdbcType=INTEGER}
