mapper.select:
select a.* from ( select r.id as receiveId, r.sender_name as senderName, r.sender_id as senderId, GET_DICTVALUE_BY_DICTKEY('GXSDM',r.sender_unit) as senderUnit, r.msg_state as msgState, r.msg_id as msgId, t.id as id, t.subject as subject, t.content as content, GET_DICTVALUE_BY_DICTKEY('XXLXDM',t.type) as msgType, GET_DICTVALUE_BY_DICTKEY('XXDJDM',t.msg_level) as msgLevel, date_format(t.create_date,'%Y-%m-%d %H%i%S') as msgDateStr, t.create_date as createDate, t.tslb as tslb, t.att_id as attId from SYS_MESSAGE t ,SYS_RECEIVE_BOX r where 1=1 and t.id = r.msg_id and r.receiver_id = #{user.userName,jdbcType=VARCHAR} and r.del = '0'
and r.msg_state = #{msgState,jdbcType=VARCHAR}
and t.msg_level = #{msgLevel,jdbcType=VARCHAR}
and t.msg_vest = #{msgVest,jdbcType=VARCHAR}
and t.type = #{msgType,jdbcType=VARCHAR}
and r.sender_name like '%' || #{senderName,jdbcType=VARCHAR} || '%'
and t.content like '%' || #{content,jdbcType=VARCHAR} || '%'
and t.tslb not in ${noTslbSql}
and t.create_date >= to_date(#{msgDateBegin},'yyyy-mm-dd')
and t.create_date < to_date(#{msgDateEnd},'yyyy-mm-dd')+1
order by ${orderByString}
) a
limit #{begin,jdbcType=INTEGER}, #{and,jdbcType=INTEGER}

select
  count(1)
from SYS_MESSAGE t ,SYS_RECEIVE_BOX r
where 1=1
    and t.ID = r.MSG_ID
    and r.RECEIVER_ID = #{user.userName,jdbcType=VARCHAR}
    and r.DEL = '0'

    and r.MSG_STATE =  #{msgState,jdbcType=VARCHAR}


    and t.MSG_LEVEL =  #{msgLevel,jdbcType=VARCHAR}


    and t.MSG_VEST =  #{msgVest,jdbcType=VARCHAR}


    and t.TYPE =  #{msgType,jdbcType=VARCHAR}


    and r.SENDER_NAME like '%' || #{senderName,jdbcType=VARCHAR} || '%'


    and t.CONTENT like '%' || #{content,jdbcType=VARCHAR} || '%'


    and t.TSLB not in ${noTslbSql}


    and t.CREATE_DATE >= to_date(#{msgDateBegin},'yyyy-mm-dd')


    and t.CREATE_DATE < to_date(#{msgDateEnd},'yyyy-mm-dd')+1

mapper.update:
update SYS_RECEIVE_BOX t
set t.DEL = '1',
t.MODIFY_PID = #{user.userName,jdbcType=VARCHAR},
t.MODIFY_DATE = now()
where t.MSG_ID = #{id,jdbcType=VARCHAR}
and t.RECEIVER_ID = #{user.userName,jdbcType=VARCHAR}

update SYS_RECEIVE_BOX t
set t.MSG_STATE = '2',
    t.MODIFY_PID = #{user.userName,jdbcType=VARCHAR},
    t.MODIFY_DATE = now()
where t.MSG_ID = #{id,jdbcType=VARCHAR}
  and t.RECEIVER_ID = #{user.userName,jdbcType=VARCHAR}
